import React, { useState, useEffect } from 'react';
import { Card, Button } from 'react-bootstrap'; // Import Button component from react-bootstrap
// import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

export default function AuthPage({ cart, setCart }) {
  const [showSignUp, setShowSignUp] = useState(false);
  const [data, setData] = useState([]);

  useEffect(() => {
    const apiUrl = 'http://localhost:3000/api/entries';

    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        setData(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleAddToCart = () => {
    const newItem = {
      id: cart.length + 1,
      name: "Anime Loot Snack",
      price: 19.99,
    };
    setCart(prevCart => [...prevCart, newItem]);
  };

  return (
    <main className="container">
      {/* ... your existing code ... */}
      <div className="boxes-container">
        <div className="image-box">
          <Card className="image-card">
            <Card.Img variant="top" src="https://w7.pngwing.com/pngs/909/655/png-transparent-one-piece-logo-monkey-d-luffy-one-piece-usopp-logo-pirate-hat-manga-jolly-roger-smiley-thumbnail.png" />
            <Button onClick={handleAddToCart} variant="primary">Add to Cart</Button> {/* Use Bootstrap Button */}
          </Card>
        </div>
        {/* ... the rest of your code ... */}
      </div>
      {/* ... the rest of your code ... */}
    </main>
  );
}
